package leetcode;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class CountUniqueCharacters828Test {
	@Test
	public void successful() {
		CountUniqueCharacters828 countUniqueCharacters828 = new CountUniqueCharacters828();
		Assertions.assertEquals(1, countUniqueCharacters828.uniqueLetterString("A"));
		int result = countUniqueCharacters828.uniqueLetterString("ABC");
		Assertions.assertEquals(10, result);
		Assertions.assertEquals(92, countUniqueCharacters828.uniqueLetterString("LEETCODE"));
	}

	@Test
	public void longString() {
		CountUniqueCharacters828 countUniqueCharacters828 = new CountUniqueCharacters828();
		Assertions.assertEquals(620286, countUniqueCharacters828.uniqueLetterString(""));
	}
}
